const products=[{id:1,name:"Wireless Headphones",price:49.99,image:"images/headphones.jpg"},{id:2,name:"Smart Watch",price:79.99,image:"images/watch.jpg"},{id:3,name:"Bluetooth Speaker",price:39.99,image:"images/speaker.jpg"},{id:4,name:"Gaming Mouse",price:29.99,image:"images/mouse.jpg"}],productGrid=document.getElementById("products"),cartItems=document.getElementById("cart-items"),cartTotal=document.getElementById("cart-total"),cartCount=document.getElementById("cart-count");let cart=[];function renderProducts(){products.forEach(e=>{const t=document.createElement("div");t.className="product",t.innerHTML=`<img loading="lazy" src="${e.image}" alt="${e.name}" /><h3>${e.name}</h3><p class="price">$${e.price.toFixed(2)}</p><button onclick="addToCart(${e.id})">Add to Cart</button>`,productGrid.appendChild(t)})}function addToCart(e){const t=products.find(t=>t.id===e);cart.push(t),updateCart()}function updateCart(){cartItems.innerHTML="";let e=0;cart.forEach((t,a)=>{e+=t.price;const n=document.createElement("li");n.innerHTML=`${t.name} - $${t.price.toFixed(2)} <button onclick="removeFromCart(${a})">x</button>`,cartItems.appendChild(n)}),cartTotal.textContent=e.toFixed(2),cartCount.textContent=cart.length}function removeFromCart(e){cart.splice(e,1),updateCart()}function checkout(){0===cart.length?alert("Your cart is empty!"):(alert("Thanks for shopping, Akash! Your order has been placed."),cart=[],updateCart())}document.addEventListener("DOMContentLoaded",renderProducts);
